import { useRef } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import Logo from "./logo.png";

function Navbar() {
  const navRef = useRef();

  const showNavbar = () => {
    navRef.current.classList.toggle("responsive_nav");
  };

  return (
    <header style={headerStyle}>
      <h3>
        <img src={Logo} alt="Logo" style={logoStyle} />
      </h3>
      <nav ref={navRef} style={navStyle}>
        <a href="/#" style={navLinkStyle}>
          Home
        </a>
        <a href="/#" style={navLinkStyle}>
          Tickets
        </a>
        <a href="/#" style={navLinkStyle}>
          Abertura de Chamados
        </a>
        <button style={closeButtonStyle} onClick={showNavbar}>
          <FaTimes />
        </button>
      </nav>
      <button style={hamburgerButtonStyle} onClick={showNavbar}>
        <FaBars />
      </button>
    </header>
  );
}

const headerStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  backgroundColor: "#ffffff",
  padding: "20px 40px",
  boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
  position: "fixed",
  top: 0,
  left: 0,
  right: 0,
};

const logoStyle = {
  maxWidth: "100%",
};

const navStyle = {
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  position: "fixed",
  top: "70px",
  left: 0,
  right: 0,
  backgroundColor: "#ffffff",
  padding: "20px",
  opacity: 0,
  visibility: "hidden",
  transition: "all 0.3s ease",
};

const navLinkStyle = {
  margin: "10px 0",
  fontSize: "20px",
  color: "#333333",
  textDecoration: "none",
  marginRight: "20px",
  transition: "all 0.3s ease",
};

const closeButtonStyle = {
  backgroundColor: "#333333",
  color: "#ffffff",
  border: "none",
  padding: "10px 20px",
  marginTop: "20px",
  borderRadius: "5px",
  cursor: "pointer",
  transition: "all 0.3s ease",
};

const hamburgerButtonStyle = {
  backgroundColor: "transparent",
  border: "none",
  cursor: "pointer",
  fontSize: "24px",
};
