import { useRef } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import Logo from "./logo.png";

function Navbar() {
  const navRef = useRef();

  const showNavbar = () => {
    navRef.current.style.opacity = navRef.current.style.opacity === "1" ? "0" : "1";
    navRef.current.style.visibility = navRef.current.style.visibility === "visible" ? "hidden" : "visible";
  };

  return (
    <header style={{display: "flex", justifyContent: "space-between", alignItems: "center", backgroundColor: "#ffffff", padding: "20px 40px", boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)", position: "fixed", top: 0, left: 0, right: 0}}>
      <h3>
        <img src={Logo} alt="Logo" style={{maxWidth: "100%"}} />
      </h3>
      <nav ref={navRef} style={{display: "flex", flexDirection: "column", alignItems: "center", position: "fixed", top: "70px", left: 0, right: 0, backgroundColor: "#ffffff", padding: "20px", opacity: 0, visibility: "hidden", transition: "all 0.3s ease"}}>
        <a href="/#" style={{margin: "10px 0", fontSize: "20px", color: "#333333", textDecoration: "none", marginRight: "20px", transition: "all 0.3s ease"}}>Home</a>
        <a href="/#" style={{margin: "10px 0", fontSize: "20px", color: "#333333", textDecoration: "none", marginRight: "20px", transition: "all 0.3s ease"}}>Tickets</a>
        <a href="/#" style={{margin: "10px 0", fontSize: "20px", color: "#333333", textDecoration: "none", marginRight: "20px", transition: "all 0.3s ease"}}>Abertura de Chamados</a>
        <button style={{backgroundColor: "#333333", color: "#ffffff", border: "none", padding: "10px 20px", marginTop: "20px", borderRadius: "5px", cursor: "pointer", transition: "all 0.3s ease"}} onClick={showNavbar}>
          <FaTimes />
        </button>
      </nav>
      <button style={{backgroundColor: "transparent", border: "none", cursor: "pointer", fontSize: "24px"}} onClick={showNavbar}>
        <FaBars />
      </button>
    </header>
  );
}

export default Navbar;
