import React, { useRef } from "react";
import Logo from "./logo.png";
import { FaBars, FaTimes } from "react-icons/fa";

const Navbar = () => {
  const navRef = useRef();

  const showNavbar = () => {
    navRef.current.classList.toggle("responsive_nav");
  };

  return (
    <header>
      <h3>
        <img src={Logo} alt="Logo" />
      </h3>
      <nav ref={navRef}>
        <a href="/#">Home</a>
        <a href="/#">Tickets</a>
        <a href="/#">Abertura de Chamados</a>
        <button className="nav-btn nav-close-btn" onClick={showNavbar}>
          <FaTimes />
        </button>
      </nav>
      <button className="nav-btn" onClick={showNavbar}>
        <FaBars />
      </button>

      <style jsx>{`
        header {
          display: flex;
          align-items: center;
          justify-content: space-between;
          height: 80px;
          padding: 0 2rem;
          background-color: var(--mainColor);
          color: var(--textColor);
        }

        nav a {
          margin: 0 1rem;
          color: var(--textColor);
          text-decoration: none;
        }

        nav a:hover {
          color: var(--secondaryColor);
        }

        header .nav-btn {
          padding: 5px;
          cursor: pointer;
          background: transparent;
          border: none;
          outline: none;
          color: var(--textColor);
          visibility: hidden;
          opacity: 0;
          font-size: 1.8rem;
        }

        header div,
        nav {
          display: flex;
          align-items: center;
        }

        @media only screen and (max-width: 1024px) {
          header .nav-btn {
            visibility: visible;
            opacity: 1;
          }

          header nav {
            position: fixed;
            top: -100vh;
            left: 0;
            height: 100%;
            width: 100%;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            gap: 1.5rem;
            background-color: var(--mainColor);
            transition: 1s;
          }

          header .responsive_nav {
            transform: translateY(100vh);
          }

          nav .nav-close-btn {
            position: absolute;
            top: 2rem;
            right: 2rem;
          }

          nav a {
            font-size: 1.5rem;
          }
        }
      `}</style>
    </header>
  );
};

export default Navbar;
