import React, { useRef } from 'react';
import Logo from "./logo.png"
import { FaBars, FaTimes } from 'react-icons/fa';
import styled from 'styled-components';

const Header = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 80px;
  padding: 0 2rem;
  background-color: var(--mainColor);
  color: var(--textColor);
`;

const Nav = styled.nav`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 1.5rem;
  background-color: var(--mainColor);
  position: fixed;
  top: 80px;
  left: 0;
  width: 100%;
  height: calc(100vh - 80px);
  transition: transform 0.3s ease;
  transform: translateY(-100%);
  
  &.responsive_nav {
    transform: translateY(0);
  }
`;

const NavLink = styled.a`
  margin: 0 1rem;
  color: var(--textColor);
  text-decoration: none;
  font-size: 1.5rem;
  
  &:hover {
    color: var(--secondaryColor);
  }
`;

const NavButton = styled.button`
  padding: 5px;
  cursor: pointer;
  background: transparent;
  border: none;
  outline: none;
  color: var(--textColor);
  font-size: 1.8rem;
`;

function Navbar() {
  const navRef = useRef();

  const showNavbar = () => {
    navRef.current.classList.toggle("responsive_nav");
  };

  return (
    <Header>
      <h3><img src={Logo} alt="Logo"/></h3>
      <Nav ref={navRef}>
        <NavLink href="/#">Home</NavLink>
        <NavLink href="/#">Tickets</NavLink>
        <NavLink href="/#">Abertura de Chamados</NavLink>
        <NavButton className="nav-btn nav-close-btn" onClick={showNavbar}>
          <FaTimes />
        </NavButton>
      </Nav>
      <NavButton className="nav-btn" onClick={showNavbar}>
        <FaBars />
      </NavButton>
    </Header>
  );
}

export default Navbar;
