import { useRef } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import Logo from "./logo.png";
import Navbar from "./Navbar";


  const showNavbar = () => {
    const navStyle = navRef.current.style;
    navStyle.display = navStyle.display === "flex" ? "none" : "flex";
  };

  const navStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "1rem 2rem",
    backgroundColor: "#fff",
    boxShadow: "0px 2px 10px rgba(0, 0, 0, 0.1)",
  };

  const navLogoStyles = {
    height: "60px",
  };

  const navLinkStyles = {
    margin: "0 1rem",
    textDecoration: "none",
    color: "#333",
    fontWeight: "bold",
  };

  const navBtnStyles = {
    border: "none",
    borderRadius: "4px",
    padding: "0.5rem 1rem",
    backgroundColor: "#333",
    color: "#fff",
    cursor: "pointer",
  };


    <header style={navStyles}>
      <h3>
        <img src={Logo} alt="Logo" style={navLogoStyles} />
      </h3>
      <nav
        ref={navRef}
        style={{
          display: "none",
          flexDirection: "column",
          alignItems: "center",
          backgroundColor: "#fff",
          position: "absolute",
          top: "100%",
          left: 0,
          width: "100%",
          padding: "1rem 0",
          boxShadow: "0px 2px 10px rgba(0, 0, 0, 0.1)",
        }}
      >
        <a href="/#" style={navLinkStyles}>
          Home
        </a>
        <a href="/#" style={navLinkStyles}>
          Tickets
        </a>
        <a href="/#" style={navLinkStyles}>
          Abertura de Chamados
        </a>
        <button className="nav-btn nav-close-btn" onClick={showNavbar}>
          <FaTimes />
        </button>
      </nav>
      <button
        className="nav-btn"
        onClick={showNavbar}
        style={navBtnStyles}
      >
        <FaBars />
      </button>
    </header>

export default Navbar;
